mapper.select:
select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true
and t.controlUnitName=#{value}
) and yearweek(str_to_date(c.creat_time, '%Y-%m-%d')) = yearweek(now())

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true
and t.controlUnitName=#{value}
) and yearweek(str_to_date(c.creat_time, '%Y-%m-%d')) = yearweek(now())-1
and str_to_date(c.creat_time, '%Y-%m-%d') < date_format(DATE_SUB(now(), interval 6 day), '%Y-%m-%d')

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true
and t.controlUnitName=#{value}
) and c.cam_state=1

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true
and t.controlUnitName=#{value}
) and c.cam_state=0

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true and t.controlUnitName in
#{item}
) and yearweek(str_to_date(c.creat_time, '%Y-%m-%d')) = yearweek(now())

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true and t.controlUnitName in
#{item}
) and yearweek(str_to_date(c.creat_time, '%Y-%m-%d')) = yearweek(now())-1
and str_to_date(c.creat_time, '%Y-%m-%d') < date_format(DATE_SUB(now(), interval 6 day), '%Y-%m-%d')

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true and t.controlUnitName in
#{item}
) and c.cam_state=1

select count(1) from tcamera c where c.cam_org_id in ( select t.org_id from torganization t where t.is_enable=true and t.controlUnitName in
#{item}
) and c.cam_state=0
